--
-- Name: update_project_metrics("uuid"); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION "public"."update_project_metrics"("target_project_id" "uuid" DEFAULT NULL::"uuid") RETURNS "void"
    LANGUAGE "plpgsql"
    AS $$
DECLARE
    project_record RECORD;
BEGIN
    FOR project_record IN 
        SELECT id FROM projects 
        WHERE (target_project_id IS NULL OR id = target_project_id)
    LOOP
        INSERT INTO project_metrics (
            project_id, 
            metric_date,
            total_tasks,
            completed_tasks,
            active_tasks,
            overdue_tasks,
            completion_percentage
        )
        SELECT 
            project_record.id,
            CURRENT_DATE,
            COUNT(pt.id),
            COUNT(CASE WHEN pt.status = 'completed' THEN 1 END),
            COUNT(CASE WHEN pt.status IN ('pending', 'in_progress') THEN 1 END),
            COUNT(CASE WHEN pt.due_date < CURRENT_DATE AND pt.status != 'completed' THEN 1 END),
            CASE 
                WHEN COUNT(pt.id) > 0 THEN 
                    ROUND((COUNT(CASE WHEN pt.status = 'completed' THEN 1 END)::DECIMAL / COUNT(pt.id)::DECIMAL) * 100, 2)
                ELSE 0 
            END
        FROM project_tasks pt
        WHERE pt.project_id = project_record.id
        ON CONFLICT (project_id, metric_date) 
        DO UPDATE SET
            total_tasks = EXCLUDED.total_tasks,
            completed_tasks = EXCLUDED.completed_tasks,
            active_tasks = EXCLUDED.active_tasks,
            overdue_tasks = EXCLUDED.overdue_tasks,
            completion_percentage = EXCLUDED.completion_percentage,
            created_at = NOW();
    END LOOP;
END;
$$;


